import styles from './stepper.css';

const Stepper = ({ step }) => {
    return (
      <div className="stepper">
        <div className={`step ${step === 1 ? 'active' : ''}`}>
          {step > 1 ? '✔️' : '1'}
        </div>
        <div className={`step ${step === 2 ? 'active' : ''}`}>
          {step > 2 ? '✔️' : '2'}
        </div>
        <div className={`step ${step === 3 ? 'active' : ''}`}>
          {step > 3 ? '✔️' : '3'}
        </div>
      </div>
    );
};

export default Stepper;
