function SliderContent({ activeIndex, sliderImage }) {
  return (
    <div className="slider-content">
      {sliderImage.map((slide, index) => (
        <div
          key={index}
          className={index === activeIndex ? "slide active" : "slide"}
        >
          <img
            src={slide.urls}
            alt=""
            style={index === 0 ? { width: "50%" } : {}}
          />
        </div>
      ))}
    </div>
  );
}

  
  export default SliderContent;
