import React, { useContext } from 'react';
import { ProductContext } from '../components/ProductContext';
import Navbar from '../components/Navbar.js';
import Footer from '../components/Footer.js';
import style from './Cart.css';
import { Link, useHistory } from 'react-router-dom';

const Cart = () => {
    const history = useHistory();

    const { cart, removeFromCart, updateQuantity } = useContext(ProductContext);
    console.log('Cart:', cart);

    const totalQuantity = cart.reduce((total, product) => total + product.quantity, 0);
    const subtotal = cart.reduce(
        (total, product) => total + product.price * product.quantity,
        0
    );

    const handleCheckout = () => {
        if (cart.length === 0) {
            alert("Oops, there are no items in the cart. Please add items before proceeding.");
        } else {
            history.push("/CheckoutForm");
        }
    };

    const handleGoShopping = () => {
        history.push("/sneakers");
    };

    return (
        <div className="cart-container">
            <Navbar />
            <div className="cart-your-cart-text">Your Cart</div>
            <div className="shopping-cart">
                <div className="cart-item-display">
                    {cart.length === 0 ? (
                        <h3>Your cart is currently empty</h3>
                    ) : (
                        <div>
                            {cart.map((product) => {
                                const totalPrice = product.price * product.quantity;
                                return (
                                    <div key={product.name} className="cart-item">
                                        <div className="item-details">
                                            <img src={product.image} alt={product.name} />
                                        </div>
                                        <div className="item-info">
                                            <div className="item-name">{product.name}</div>
                                            <div className='item-infor'>
                                                <p>Price: ${product.price}&nbsp;&nbsp;&nbsp;&nbsp;</p>
                                                <p>Size: {product.size}&nbsp;&nbsp;&nbsp;&nbsp;</p>
                                                <p>Color: {product.color}</p>
                                            </div>
                                            <div className="item-quantity">
                                                <button onClick={() => updateQuantity(product, product.quantity - 1)}>
                                                    -
                                                </button>
                                                <p>{product.quantity}</p>
                                                <button onClick={() => updateQuantity(product, product.quantity + 1)}>
                                                    +
                                                </button>
                                                <div className="item-price">
                                                    <p>Total Price: ${totalPrice}</p>
                                                </div>
                                            </div>
                                            <button className="remove-button" onClick={() => removeFromCart(product)}>
                                                Remove
                                            </button>
                                        </div>
                                    </div>
                                );
                            })}
                        </div>
                    )}
                </div>
            </div>
            <div className="right-checkout-section">
                <h2>Subtotal</h2>
                <p>${subtotal}</p>
                <p>Total Items: {totalQuantity}</p>
                {cart.length === 0 ? (
                    <div className="empty-cart-message">
                        <p>Oops, there are no items in the cart.</p>
                        <p>You can add items <Link to="/">here</Link>!</p>
                    </div>
                ) : (
                    <button onClick={handleCheckout}>Checkout</button>
                )}
                <button onClick={handleGoShopping}>Go Shopping</button>
            </div>
            <Footer />
        </div>
    );
};

export default Cart;
